#!/bin/bash ## BEGIN INIT INFO # Provides: browserquest # Default-Start: 2 3 4 5 # Default-Stop: 0 1 2 3 4 6 # Required-Start: network # ## END INIT INFO # browserquest: Start BrowserQuest server # # chkconfig: 2345 80 99 # # description: Starts the BrowserQuest server #
# Source function library. . /etc/init.d/functions
# Source networking configuration. . /etc/sysconfig/network
# Check that networking is up. [ "$NETWORKING" = "no" ] && exit 0
NODE="/usr/bin/node" prog=$(basename $NODE)
lockfile="/var/lock/subsys/node" pidfile="/var/run/browserquest.pid"
base=${0##*/}
start() { echo -n "Starting BrowserQuest"
# Start Node.js cd /root/browserquest-BrowserQuest-629882b $NODE server/js/main.js > /var/log/nodejs.log 2> /var/log/nodejs.error & retval=$? echo $! > $pidfile echo [ $retval -eq 0 ] && touch $lockfile return $retval }
stop() { echo -n "Stopping BrowserQuest" killproc -p $pidfile $prog retval=$? echo [ $retval -eq 0 ] && rm -f $lockfile return $retval }
restart() { configtest_q || return 6 stop start }
rh_status() { status $prog }
rh_status_q() { rh_status >/dev/null 2>&1 }
case "$1" in start) rh_status_q && exit 0 $1 ;; stop) rh_status_q || exit 0 $1 ;; restart) $1 ;; status|status_q) rh_$1 ;; condrestart|try-restart) rh_status_q || exit 7 restart ;; *) echo $"Usage: $0 {start|stop|status|restart}" exit 2 esac
